mapper.update:
UPDATE free_board
SET views = views + 1
WHERE free_board_id = #{freeBoardId}

mapper.select:
SELECT free_board_id, title, views, created_at, modified_at, ct.value as 'category', m.name as 'member.name', m.member_id as 'member.memberId', (SELECT count(*) > 0 from file) as have_file FROM free_board fb LEFT JOIN category ct on fb.category_id = ct.category_id LEFT JOIN member m on m.member_id = fb.member_id
ORDER BY ${page.orderColumn} ${page.orderType}
ORDER BY fb.created_at DESC
LIMIT #{page.offset}, #{page.size}

SELECT *
FROM free_board
WHERE
    free_board_id = #{freeBoardId}
    AND member_id = #{memberId}
