mapper.select:
SELECT ib.*, m.name AS 'member.name', m.member_id AS 'member.memberId', (SELECT count(*) FROM inquire_answer AS ia WHERE ia.inquire_board_id = ib.inquire_board_id) AS isAnswered FROM inquire_board AS ib LEFT JOIN member m on m.member_id = ib.member_id
ORDER BY ${page.orderColumn} ${page.orderType}
ORDER BY ib.modified_at DESC
LIMIT #{page.offset}, #{page.size}

SELECT *
FROM inquire_board ib
WHERE ib.inquire_board_id = #{inquireBoardId}
  AND ib.member_id = #{memberId};
